import React, { useState, useEffect } from "react";
import axios from "axios";
import EditClientForm from "./EditClientForm";

function EditClientPage({ match }) {
  const [editingClient, setEditingClient] = useState(null);
  const clientId = match.params.id;

  useEffect(() => {
    fetchClientData();
  }, [clientId]);

  const fetchClientData = async () => {
    try {
      const response = await axios.get(`http://localhost:3000/api/clients/${clientId}`);
      setEditingClient(response.data);
    } catch (error) {
      console.error("Error fetching client data:", error);
    }
  };

  const handleEditSubmit = async (formData) => {
    try {
      await axios.put(`http://localhost:3000/api/clients/${clientId}`, formData);
      fetchClientData();
    } catch (error) {
      console.error("Error updating client:", error);
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h2 className="text-3xl font-semibold mb-4">Modifier Client</h2>
      <EditClientForm onSubmit={handleEditSubmit} clientData={editingClient} />
    </div>
  );
}

export default EditClientPage;
