import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";

function Consulter(props) {
    const location = useLocation();
    const state = location.state || {};
    const { clientName, clientAddress, numberInvoice, invoiceItems } = state;

    return (
        <>
            {/* Affichage des données de la facture ici */}
            <h2>Nom du client: {clientName}</h2>
            <p>Adresse du client: {clientAddress}</p>
            <p>Numéro de facture: {numberInvoice}</p>
            <ul>
                {invoiceItems.map((item, index) => (
                    <li key={index}>
                        {item.description} - Quantité: {item.quantity} - Prix: {item.price} - Montant: {item.amount}
                    </li>
                ))}
            </ul>
        </>
    );
}

export default Consulter;
