import React, { useState, useEffect } from "react";
import axios from "axios";

function Facturation() {
  const [clientName, setClientName] = useState("");
  const [clientAddress, setClientAddress] = useState("");
  const [articles, setArticles] = useState([]);
  const [selectedArticle, setSelectedArticle] = useState("");

  useEffect(() => {
    // Ici, vous pouvez appeler votre fonction pour récupérer les articles
    fetchArticles();
  }, []);

  const fetchArticles = async () => {
    try {
      // Remplacez l'URL ci-dessous par l'URL de votre endpoint pour récupérer les articles depuis votre backend
      const response = await axios.get("http://localhost:3000/api/articles");
      setArticles(response.data);
    } catch (error) {
      console.error("Error fetching articles:", error);
    }
  };

  return (
    <div>
      <h2>Facturation</h2>
      <label>Nom du client:</label>
      <input
        type="text"
        value={clientName}
        onChange={(e) => setClientName(e.target.value)}
      />
      <label>Adresse du client:</label>
      <input
        type="text"
        value={clientAddress}
        onChange={(e) => setClientAddress(e.target.value)}
      />
      <label>Choisir un article:</label>
      <select
        value={selectedArticle}
        onChange={(e) => setSelectedArticle(e.target.value)}
      >
        <option value="">Sélectionnez un article</option>
        {articles.map((article) => (
          <option key={article.id} value={article.id}>
            {article.name} - {article.price} {article.currency}
          </option>
        ))}
      </select>
    </div>
  );
}

export default Facturation;
