import React, { useState, useEffect } from "react";
import axios from "axios";
console.log("REACT_APP_API_BASE_URL:", process.env.REACT_APP_API_BASE_URL);

function App() {
  const [clients, setClients] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    address: "",
    email: "",
    phone: ""
  });

  useEffect(() => {
    fetchClients();
  }, []);

  const fetchClients = async () => {
    try {
      const response = await axios.get("http://localhost/api/clients");
      setClients(response.data);
    } catch (error) {
      console.error("Error fetching clients:", error);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Use the correct API base URL from environment variables
      const apiBaseUrl = process.env.REACT_APP_API_BASE_URL;
      console.log("API Base URL:", apiBaseUrl);

      // Make sure apiBaseUrl is defined before making the request
      if (!apiBaseUrl) {
        console.error("API Base URL is not defined");
        return;
      }

      await axios.post(`${apiBaseUrl}/clients`, formData);
      fetchClients();
      setFormData({ name: "", address: "", email: "", phone: "" });
    } catch (error) {
      console.error("Error creating client:", error);
    }
  };

  return (
    <>
      <main className="m-5 p-5 md:max-w-xl md:mx-auto lg:max-w-2xl xl:max-w-4xl bg-white rounded shadow">
        <div className="flex flex-col justify-center">
          <form onSubmit={handleSubmit} className="md:grid grid-cols-1 gap-10">
            <div className="flex flex-col">
              <label htmlFor="name">Votre Nom</label>
              <input
                type="text"
                name="name"
                id="name"
                placeholder="Votre nom"
                autoComplete="off"
                value={formData.name}
                onChange={handleChange}
              />
            </div>

            <div className="flex flex-col">
              <label htmlFor="address">Votre Adresse</label>
              <input
                type="text"
                name="address"
                id="address"
                placeholder="Votre adresse"
                autoComplete="off"
                value={formData.address}
                onChange={handleChange}
              />
            </div>

            <div className="flex flex-col">
              <label htmlFor="email">Votre Email</label>
              <input
                type="email"
                name="email"
                id="email"
                placeholder="Votre email"
                autoComplete="off"
                value={formData.email}
                onChange={handleChange}
              />
            </div>

            <div className="flex flex-col">
              <label htmlFor="phone">Votre Téléphone</label>
              <input
                type="text"
                name="phone"
                id="phone"
                placeholder="Votre téléphone"
                autoComplete="off"
                value={formData.phone}
                onChange={handleChange}
              />
            </div>

            <button
              type="submit"
              className="bg-blue-500 text-white font-bold py-2 px-8 rounded shadow border-2 border-blue-500 
              hover:bg-transparent hover:text-blue-500 
              transition-all duration-300"
            >
              Ajouter Client
            </button>
          </form>

          <div>
            <h2>Liste des Clients</h2>
            <ul>
              {clients.map((client) => (
                <li key={client.id}>
                  {client.name} - {client.email}
                </li>
              ))}
            </ul>
          </div>
        </div>
      </main>
    </>
  );
}

export default App;
