function App() {
  const handlePrint = () => {
    window.print()
  }
  return (
    <>
      <main className="p-5 lg:max-w-xl lg:mx-auto bg-white rounded shadow">
          <header className="flex flex-col items-center justify-center mb-5">
            <div>
              <h1 className="font-blod uppercase tracking_wide text-4xl mb-3">Facturation</h1>
            </div>
            <div>
              <ul className="flex items-center justify-between flex-wrap">
                <li><button onClick={handlePrint} className="btn btn-print">Imprimer </button></li>
                <li><button className="btn btn-download">Installer</button></li>
                <li><button className="btn btn-send">Envoyer</button></li>
              </ul>
            </div>
          </header>
          {/* End of header */}

          {/* Your details */}
          <section className="flex flex-col items-end justify-end">
            <input type="text" name="text" id="text"
            placeholder="Enter your name" required />
            <h2 className="text-xl uppercase">Oumaima</h2>
            <p>Your Adress</p>
         </section>
          {/* End of your details */}

          {/* Client details */}
          <section className="mt-5 "> 
            <h2 className="text-xl uppercase">Client's Name</h2>
            <p>Client's adress</p>
          </section>
          {/* End of client details */}

          {/* Dates */}
          <article className="my-5 flex flex-col items-end justify-end">
            <ul>
              <li><span>Numero de facturation</span></li>
              <li>Date du facturation</li>
              <li>Date d'échéance</li>
            </ul>
          </article>

          {/* End of dates */}

          {/* Table */}

          {/* End of table */}

          {/* Notes */}
          <section>
          {/* Textarea */}
          <p>Note to the client...</p>

          </section>
          {/* End of notes */}

          {/* Footer */}
          <footer>
            <ul>
              <li>Votre Nom</li>
              <li>Votre Email</li>
              <li>Voitre Numero</li>
              <li>Bank</li>
              <li>Website</li>
            </ul>
          </footer>

          {/* End of footer */}








      </main>
    </>
  );
}

export default App;
