import React, { useState, useEffect } from 'react';
import axios from 'axios';

const FacturationForm = () => {
    const [facturations, setFacturations] = useState([]);

    useEffect(() => {
        // Fetch all facturations from the backend when the component mounts
        axios.get('/fact')
            .then(response => {
                setFacturations(response.data);
            })
            .catch(error => {
                console.error('Error fetching facturations:', error);
            });
    }, []); // Empty dependency array ensures the effect runs only once on mount

    return (
        <div>
            <h2>List of Factures</h2>
            <table>
                <thead>
                    <tr>
                        <th>Facture Number</th>
                        <th>Facture Date</th>
                        <th>Client</th>
                    </tr>
                </thead>
                <tbody>
                    {facturations.map(facturation => (
                        <tr key={facturation.id}>
                            <td>{facturation.numeroFacture}</td>
                            <td>{facturation.dateFacture}</td>
                            <td>{facturation.client.name}</td> {/* Assuming client object has a 'name' property */}
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default FacturationForm;
