import { useState, useEffect } from "react";
import axios from "axios";
import ClientTable from "./components/ClientTable"; // Assuming you have a component for rendering client table
import ArticleTable from "./components/ArticleTable"; // Assuming you have a component for rendering article table
import FacturationForm from "./components/FacturationForm"; // Assuming you have a component for creating facturations

function App() {
  const [clients, setClients] = useState([]);
  const [articles, setArticles] = useState([]);
  const [selectedClient, setSelectedClient] = useState(null);
  const [selectedArticles, setSelectedArticles] = useState([]);

  useEffect(() => {
    fetchClients();
    fetchArticles();
  }, []);

  const fetchClients = async () => {
    try {
      const response = await axios.get(`${process.env.REACT_APP_API_BASE_URL}/client`);
      setClients(response.data);
    } catch (error) {
      console.error('Error fetching clients:', error);
    }
  };

  const fetchArticles = async () => {
    try {
      const response = await axios.get(`${process.env.REACT_APP_API_BASE_URL}/article`);
      setArticles(response.data);
    } catch (error) {
      console.error('Error fetching articles:', error);
    }
  };

  const handleClientSelect = (client) => {
    setSelectedClient(client);
  };

  const handleArticleSelect = (article) => {
    setSelectedArticles([...selectedArticles, article]);
  };

  const handleFacturationSubmit = async () => {
    try {
      // Make POST request to create facturation with selected client and articles
      // axios.post(`${process.env.REACT_APP_API_BASE_URL}/fact`, { client: selectedClient, articles: selectedArticles });
      console.log("Facturation created successfully!");
    } catch (error) {
      console.error('Error creating facturation:', error);
    }
  };

  return (
    <>
      <main>
        <h1>Choose Client:</h1>
        <ClientTable clients={clients} onSelect={handleClientSelect} />
        <h1>Choose Articles:</h1>
        <ArticleTable articles={articles} onSelect={handleArticleSelect} />
        <FacturationForm onSubmit={handleFacturationSubmit} />
      </main>
    </>
  );
}

export default App;
