import React from 'react';

export default function Footer({ name, email, website, phone, bankName }) {
  return (
    <>
      <footer className='footer border-t-2 border-gray'>
        <ul className="flex flex-wrap items-center justify-center">
          <li><span className="font-bold">Votre Nom:</span> {name}</li>
          <li><span className="font-bold">Votre Email:</span> {email}</li>
          <li><span className="font-bold">Votre Numero:</span> {phone}</li>
          <li><span className="font-bold">Bank:</span> {bankName}</li>
          <li><span className="font-bold">Website:</span> <a href={website} target="_blank" rel="noopener noreferrer">{website}</a></li>
        </ul>
      </footer>
    </>
  );
}
