import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";

function Facturation(props) {
    const location = useLocation();
  const state = location.state || {};
  const { clientName, clientAddress } = state;


  const [articles, setArticles] = useState([]);
  const [selectedArticle, setSelectedArticle] = useState("");

  useEffect(() => {
    // Ici, vous pouvez appeler votre fonction pour récupérer les articles
    fetchArticles();
  }, []);

  const fetchArticles = async () => {
    try {
      // Remplacez l'URL ci-dessous par l'URL de votre endpoint pour récupérer les articles depuis votre backend
      const response = await axios.get("http://localhost:3000/api/article");
      setArticles(response.data);
    } catch (error) {
      console.error("Error fetching articles:", error);
    }
  };

  const handlePrint = () => {
    window.print();
  };

  return (
    <>
      <main className="m-5 p-5 md:max-w-xl md:mx-auto lg:max-w-2xl xl:max-w-4xl bg-white rounded shadow">
        <div>
          <h1 className="text-2xl font-semibold mb-5">Aperçu de la facture</h1>
          <div className="mb-5">
            <label className="block mb-2">Nom du client:</label>
            <p className="border border-gray-300 p-2">{clientName}</p>
          </div>
          <div className="mb-5">
            <label className="block mb-2">Adresse du client:</label>
            <p className="border border-gray-300 p-2">{clientAddress}</p>
          </div>
          <div className="mb-5">
            <label className="block mb-2">Choisir un article:</label>
            <select
              className="border border-gray-300 p-2"
              value={selectedArticle}
              onChange={(e) => setSelectedArticle(e.target.value)}
            >
              <option value="">Sélectionnez un article</option>
              {/* Ajoutez ici vos options d'article */}
            </select>
          </div>
          <div className="mb-5">
            <label className="block mb-2">Quantité:</label>
            <input
              type="number"
              className="border border-gray-300 p-2"
              value={selectedArticle}
              onChange={(e) => setSelectedArticle(e.target.value)}
            />
          </div>
          <div className="flex justify-end">
            <button
              onClick={handlePrint}
              className="bg-blue-500 text-white font-bold py-2 px-8 rounded shadow border-2 border-blue-500 hover:bg-transparent hover:text-blue-500 transition-all duration-300"
            >
              Imprimer la facture
            </button>
          </div>
        </div>
      </main>
    </>
  );
}


export default Facturation;
