import { useState } from "react";
import ClientDetails from "./components/ClientDetails";
import Dates from "./components/Dates";
import Footer from "./components/Footer";
import Heeader from "./components/Heeader";
import MainDetails from "./components/MainDetails";
import Notes from "./components/Notes";
import Table from "./components/Table";

function App() {
  const [showInvoice, setShowInvoice] = useState(false)
 const [name, setName] = useState("")
 const [address, setAddress] = useState("")
 const [email, setEmail] = useState("")
 const [phone, setPhone] = useState("")
 const [bankName, setBankName] = useState("")
 const [website, setWebsite] = useState("")
 const [clientName, setCleintName] = useState("")
 const [clientAdress, setClientAdress] = useState("")
 const [invoiceNumber, setInvoiceNumber] = useState("")
 const [invoiceDate, setInvoiceDate] = useState("")
 const [dueDate, setDueDate] = useState("")
 const [notes, setNotes] = useState("")



  const handlePrint = () => {
    window.print()
  }
  return (
    <>
      <main className="m-5 p-5 xl:max-w-4xl xl:mx-auto bg-white rounded shadow">
        {showInvoice ? <div>
          <Heeader handlePrint={handlePrint}/>
        <MainDetails name={name} address={address}/>
        <ClientDetails clientName={clientName} clientAdress={clientAdress}/>
        <Dates invoiceNumber={invoiceNumber} invoiceDate= {invoi}/>
        <Table/>
         <Notes/>
        <Footer name={name} address={address} website= {website} email= {email} />
        <button onClick={() =>setShowInvoice(false)}
         className="mt-5 bg-blue-500 text-white font-bold py-2 px-8 rounded shadow border-2 border-blue-500 
          hover:bg-transparent hover:text-bleu-500 
          transition-all duration-300"> Changer Informations</button>
        </div>: (
          <>
          <div className="flex flex-col justify-center">
            <label htmlFor="name">Votre Nom</label>
          <input type="text" name="text" id="name" placeholder="Votre nom" autoComplete="off" value={name} onChange={(e) => setName(e.target.value)}/>

          <label htmlFor="adress">Votre Adresse</label>
          <input type="text" name="address" id="adress" placeholder="Votre adresse" autoComplete="off" value={address} onChange={(e) => setAddress(e.target.value)}/>

          <label htmlFor="email">Votre Email</label>
          <input type="email" name="email" id="email" placeholder="Votre email" autoComplete="off" value={email} onChange={(e) => setEmail(e.target.value)}/>

          <label htmlFor="website">Votre WebSite</label>
          <input type="url" name="website" id="website" placeholder="Votre website" autoComplete="off" value={website} onChange={(e) => setWebsite(e.target.value)}/>

          <label htmlFor="phone">Votre téléphone</label>
          <input type="text" name="phone" id="phone" placeholder="Votre téléphone" autoComplete="off" value={phone} onChange={(e) => setPhone(e.target.value)}/>

          <label htmlFor="bankName">Votre Bank</label>
          <input type="text" name="bank" id="bank" placeholder="Votre bank" autoComplete="off" value={bankName} onChange={(e) => setBankName(e.target.value)}/>

          <label htmlFor="ClientName">Votre nom de client</label>
          <input type="text" name="clientName" id="clientName" placeholder="Votre nom de client" autoComplete="off" value={clientName} onChange={(e) => setCleintName(e.target.value)}/>

          <label htmlFor="ClientAddress">Votre adresse de client</label>
          <input type="text" name="clientAddress" id="clientAddress" placeholder="Votre adresse de client" autoComplete="off" value={clientAdress} onChange={(e) => setClientAdress(e.target.value)}/>

          <label htmlFor="invoiceNumver">Votre numero du facture</label>
          <input type="text" name="invoiceNumber" id="invoiceNumber" placeholder="Votre numero du facture" autoComplete="off" value={invoiceNumber} onChange={(e) => setInvoiceNumber(e.target.value)}/>

          <label htmlFor="invoiceDate"> date du facture</label>
          <input type="date" name="invoiceDate" id="invoiceDate" placeholder="Votre date du facture" autoComplete="off" value={invoiceDate} onChange={(e) => setInvoiceDate(e.target.value)}/>

          <label htmlFor="DueDate">Due Date</label>
          <input type="date" name="DueDate" id="DueDate" placeholder="Due Date" autoComplete="off" value={dueDate} onChange={(e) => setDueDate(e.target.value)}/>

          <label htmlFor="notes">Notes</label>
          <textarea name="notes" id="notes" cols="30" rows="10" placeholder="notes pour le client" value={notes} onChange={e => setNotes(e.target.value)}></textarea>






          <button onClick={()=> setShowInvoice(true)}
          className="bg-blue-500 text-white font-bold py-2 px-8 rounded shadow border-2 border-blue-500 
          hover:bg-transparent hover:text-bleu-500 
          transition-all duration-300">Aperçu de la facture</button>

          </div>
          </>
        )}
      </main>
    </>
  );
}

export default App;
