import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link, useNavigate, useLocation } from "react-router-dom";

function ClientTable() {
  const [clients, setClients] = useState([]);
  const navigate = useNavigate();
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  const editMsg = searchParams.get('editMsg');
  const clientName = searchParams.get('clientName');

  useEffect(() => {
    fetchClients();
  }, []);

  const fetchClients = async () => {
    try {
      const response = await axios.get("http://localhost:3000/api/clients");
      setClients(response.data);
    } catch (error) {
      console.error("Error fetching clients:", error);
    }
  };

  // Other functions...

  return (
    <>
      <main className="container mx-auto px-4 py-8">
        <h2 className="text-3xl font-semibold mb-4">Liste des Clients</h2>
        <Link to='/client/create'>Ajouter Client</Link>
        {editMsg && 
        <div style={{backgroundColor: '#34cd60', color: '#fff', padding:'10px', borderRadius: '5px'}}>
          Le Client {clientName} est modifié avec succès
        </div>}
        {/* Other content... */}
      </main>
    </>
  );
}

export default ClientTable;
