import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom"; // Importez useNavigate depuis react-router-dom

function FormClient() {
  const [clients, setClients] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    address: "",
    email: "",
    phone: ""
  });

  const navigate = useNavigate(); // Initialisez useNavigate

  useEffect(() => {
    fetchClients();
  }, []);

  const fetchClients = async () => {
    try {
      const response = await axios.get("http://localhost:3000/api/clients");
      setClients(response.data);
    } catch (error) {
      console.error("Error fetching clients:", error);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:3000/api/clients', formData);
      fetchClients();
      setFormData({ name: "", address: "", email: "", phone: "" });

      // Naviguer vers la page du tableau après l'ajout du client
      navigate("/client"); 
    } catch (error) {
      console.error("Error creating client:", error);
    }
  };

  return (
    <>
      <main className="m-5 p-5 md:max-w-xl md:mx-auto lg:max-w-2xl xl:max-w-4xl bg-white rounded shadow">
        <div className="flex flex-col justify-center">
          <form onSubmit={handleSubmit} className="md:grid grid-cols-1 gap-10">
            {/* Champs du formulaire ici */}

            <button
              type="submit"
              className="bg-yellow-500 font-bold py-2 px-8 rounded shadow border-2 border-yellow-500 
              hover:bg-transparent hover:text-yellow-500 
              transition-all duration-300"
            >
              Ajouter Client
            </button>
          </form>
        </div>
      </main>
    </>
  );
}

export default FormClient;
