import React, { useState, useEffect } from "react";
import { useLocation,Link } from "react-router-dom";
import axios from "axios";

function Consulter(props) {

    const location = useLocation();
    const state = location.state || {};
    const { numberInvoice, invoiceItems } = state;
    const [clientDetails, setClientDetails] = useState({});
    const [articleDetails, setArticleDetails] = useState([]);

    const handlePrint = () => {
        window.print()
      }

      
    
      useEffect(() => {
        async function fetchData() {
            try {
                // Récupérer les détails du client
                const clientId = invoiceItems[1].clientId; // Supposons que l'ID du client soit stocké dans le premier élément de invoiceItems
                const clientResponse = await axios.get(`http://localhost:3000/api/clients/${clientId}`);
                setClientDetails(clientResponse.data);

                // Récupérer les détails de chaque article
                const articlePromises = invoiceItems.map(item => axios.get(`http://localhost:3000/api/articles/${item.articleId}`));
                const articleResponses = await Promise.all(articlePromises);
                const articlesData = articleResponses.map(response => response.data);
                setArticleDetails(articlesData);
            } catch (error) {
                console.error("Error fetching data:", error);
            }
        }

        fetchData();
    }, [invoiceItems]);
    return (
        <>
            <main className="m-5 p-5 md:max-w-xl md:mx-auto lg:max-w-2xl xl:max-w-4xl bg-white rounded shadow">
                <div className="invoice__preview bg-white p-5 rounded-2xl border-4 border-blue-200">
                <ul className="flex items-center justify-between flex-wrap">
    <li>
        <button
            onClick={handlePrint}
            className="bg-gray-500 text-white font-bold py-2 px-8 rounded shadow border-2 border-gray-500 hover:bg-transparent hover:text-gray-500 transition-all duration-300"
        >
            Print
        </button>
    </li>
    
</ul>
                    <div  className="p-5">
                        <header className="flex flex-col items-center justify-center mb-5 xl:flex-row xl:justify-between">
                            <div>
                                <h1 className="font-bold uppercase tracking-wide text-4xl mb-3">
                                    Facture
                                </h1>
                            </div>
                        </header>
                        <section className="flex flex-col items-end justify-end">
                            <h2 className="font-bold text-3xl uppercase mb-1">LIMIMA</h2>
                            <p>Msaken Sousse</p>
                        </section>
                        <section className="mt-10">
                            <h2 className="text-2xl uppercase font-bold mb-1">{clientDetails.clientName}</h2>
                            <p>{clientDetails.clientAddress}</p>
                        </section>
                        <article className="mt-10 mb-14 flex items-end justify-end">
                            <ul>
                                <li className="p-1">
                                    <span className="font-bold">Le nombre de Facture:</span> 
                                    <input
                                        type="text"
                                        value={numberInvoice}
                                        placeholder="Saisissez le nombre de facture"
                                    />
                                </li>
                                <li className="p-1 bg-gray-100">
                                    <span className="font-bold">La Date du Facture:</span> {new Date().toISOString().slice(0,10)}
                                </li>
                            </ul>
                        </article>
                       
                        <table className="w-full mb-4">
                            <thead>
                                <tr className="bg-gray-100">
                                    <th className="px-4 py-2">Description</th>
                                    <th className="px-4 py-2">Quantité</th>
                                    <th className="px-4 py-2">Prix</th>
                                    <th className="px-4 py-2">Montant</th>
                                </tr>
                            </thead>
                            <tbody>
                            {invoiceItems && invoiceItems.map((item, index) => (
    <tr key={index}>
        <td className="border px-4 py-2">{item.description}</td>
        <td className="border px-4 py-2">{item.quantity}</td>
        <td className="border px-4 py-2">{item.price}</td>
        <td className="border px-4 py-2">{item.amount}</td>
    </tr>
))}
                            </tbody>
                        </table>
                        
                    </div>
                </div>
            </main>
        </>
    );
}

export default Consulter;
