import React, { useState, useEffect } from 'react';
import axios from 'axios';

const FacturationForm = () => {
    const [facturations, setFacturations] = useState([]);

    useEffect(() => {
        // Fetch all facturations from the backend when the component mounts
        axios.get('http://localhost:3000/api/fact')
            .then(response => {
                setFacturations(response.data);
            })
            .catch(error => {
                console.error('Error fetching facturations:', error);
            });
    }, []); // Empty dependency array ensures the effect runs only once on mount

    const handleDelete = (id) => {
        // Send a delete request to the backend to delete the facturation with the specified ID
        axios.delete(`http://localhost:3000/api/fact/${id}`)
            .then(response => {
                console.log('Facturation deleted successfully');
                // After deletion, fetch the updated list of facturations from the backend
                axios.get('/fact')
                    .then(response => {
                        setFacturations(response.data);
                    })
                    .catch(error => {
                        console.error('Error fetching facturations after deletion:', error);
                    });
            })
            .catch(error => {
                console.error('Error deleting facturation:', error);
            });
    };

    return (
        <div>
            <h2>List of Factures</h2>
            <table>
                <thead>
                    <tr>
                        <th>Facture Number</th>
                        <th>Facture Date</th>
                        <th>Client</th>
                        <th>Articles</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {facturations.map(facturation => (
                        <tr key={facturation.id}>
                            <td>{facturation.numeroFacture}</td>
                            <td>{facturation.dateFacture}</td>
                            <td>{facturation.client.name}</td> {/* Assuming client object has a 'name' property */}
                            <td>
                                <ul>
                                    {facturation.articles.map(article => (
                                        <li key={article.id}>
                                            {article.name} - {article.reference}
                                        </li>
                                    ))}
                                </ul>
                            </td>
                            <td>
                                <button onClick={() => handleDelete(facturation.id)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default FacturationForm;
