import React, { useState, useEffect } from "react";
import axios from "axios";
import './ClientTable.css';
import EditClientForm from "./EditClientForm";

function ClientTable() {
  const [clients, setClients] = useState([]);
  const [showEditForm, setShowEditForm] = useState(false);
  const [editingClientId, setEditingClientId] = useState(null);

  useEffect(() => {
    fetchClients();
  }, []);

  const fetchClients = async () => {
    try {
      const response = await axios.get("http://localhost:3000/api/clients");
      setClients(response.data);
    } catch (error) {
      console.error("Error fetching clients:", error);
    }
  };

  const handleEdit = (clientId) => {
    setEditingClientId(clientId);
    setShowEditForm(true); // Afficher le formulaire de modification
  };

  const handleDelete = async (clientId) => {
    try {
      await axios.delete(`http://localhost:3000/api/clients/${clientId}`);
      fetchClients();
    } catch (error) {
      console.error("Error deleting client:", error);
    }
  };

  const handleShow = (clientId) => {
    // Logique pour afficher les détails du client avec l'ID clientId
  };

  return (
    <>
      <main className="container mx-auto px-4 py-8">
        <h2 className="text-3xl font-semibold mb-4">Liste des Clients</h2>
        {showEditForm && <EditClientForm clientId={editingClientId} />} {/* Afficher le formulaire de modification s'il est visible */}
        <div className="overflow-x-auto">
          <table className="table-auto w-full border-collapse">
            <thead>
              <tr className="text-left border-b border-gray-300">
                <th className="px-4 py-2">Nom</th>
                <th className="px-4 py-2">Email</th>
                <th className="px-4 py-2">Adresse</th>
                <th className="px-4 py-2">Téléphone</th>
                <th className="px-4 py-2">Action</th>
              </tr>
            </thead>
            <tbody>
              {clients.map((client, index) => (
                <tr key={client.id} className={index % 2 === 0 ? "bg-gray-50" : "bg-white"}>
                  <td className="border px-4 py-2">{client.name}</td>
                  <td className="border px-4 py-2">{client.email}</td>
                  <td className="border px-4 py-2">{client.address}</td>
                  <td className="border px-4 py-2">{client.phone}</td>
                  <td className="border px-4 py-2">
                    <button onClick={() => handleEdit(client.id)} 
                      type="submit"
                      className="bg-blue-500 font-bold py-2 px-8 rounded shadow border-2 border-blue-500 
                      hover:bg-transparent hover:text-blue-500 
                      transition-all duration-300"
                    >
                      Modifier
                    </button>
                    <button onClick={() => handleDelete(client.id)} className="bg-red-500 font-bold py-2 px-8 rounded shadow border-2 border-red-500 
                      hover:bg-transparent hover:text-red-500 
                      transition-all duration-300">
                      Supprimer
                    </button>
                    <button onClick={() => handleShow(client.id)} className="bg-green-500 font-bold py-2 px-8 rounded shadow border-2 border-green-500 
                      hover:bg-transparent hover:text-green-500 
                      transition-all duration-300">
                      Afficher
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </main>
    </>
  );
}

export default ClientTable;
