import React, { useState, useEffect } from "react";
import axios from "axios";
import './ClientTable.css';
import EditClientForm from "./EditClientForm";

function ClientTable() {
  const [clients, setClients] = useState([]);
  const [editingClient, setEditingClient] = useState(null);

  useEffect(() => {
    fetchClients();
  }, []);

  const fetchClients = async () => {
    try {
      const response = await axios.get("http://localhost:3000/api/clients");
      setClients(response.data);
    } catch (error) {
      console.error("Error fetching clients:", error);
    }
  };

  const handleEdit = (client) => {
    setEditingClient(client);
  };

  const handleEditSubmit = async (formData) => {
    try {
      await axios.put(`http://localhost:3000/api/clients/${editingClient.id}`, formData);
      fetchClients();
      setEditingClient(null); // Réinitialiser l'état d'édition après la soumission réussie
    } catch (error) {
      console.error("Error updating client:", error);
    }
  };

  const handleDelete = async (clientId) => {
    try {
      await axios.delete(`http://localhost:3000/api/clients/${clientId}`);
      fetchClients();
    } catch (error) {
      console.error("Error deleting client:", error);
    }
  };

  return (
    <>
      <main className="container mx-auto px-4 py-8">
        <h2 className="text-3xl font-semibold mb-4">Liste des Clients</h2>
        <div className="overflow-x-auto">
          <table className="table-auto w-full border-collapse">
            {/* Votre tableau de clients */}
          </table>
        </div>
        <div className="mt-4">
          {editingClient && (
            <>
              <h3 className="text-xl font-semibold mb-2">Modifier Client</h3>
              <EditClientForm onSubmit={handleEditSubmit} clientData={editingClient} />
            </>
          )}
        </div>
      </main>
    </>
  );
}

export default ClientTable;
