import React, { useState, useEffect } from "react";
import axios from "axios";

function ClientTable() {
  const [clients, setClients] = useState([]);

  useEffect(() => {
    fetchClients();
  }, []);

  const fetchClients = async () => {
    try {
      const response = await axios.get("http://localhost:3000/api/clients");
      setClients(response.data);
    } catch (error) {
      console.error("Error fetching clients:", error);
    }
  };

  const handleEdit = (clientId) => {
    // Logique pour l'édition du client avec l'ID clientId
  };

  const handleDelete = async (clientId) => {
    try {
      await axios.delete(`http://localhost:3000/api/clients/${clientId}`);
      fetchClients();
    } catch (error) {
      console.error("Error deleting client:", error);
    }
  };

  const handleShow = (clientId) => {
    // Logique pour afficher les détails du client avec l'ID clientId
  };

  return (
    <>
      <main className="m-5 p-5 md:max-w-xl md:mx-auto lg:max-w-2xl xl:max-w-4xl bg-white rounded shadow">
        <h2>Liste des Clients</h2>
        <table>
          <thead>
            <tr>
              <th>Nom</th>
              <th>Email</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {clients.map((client) => (
              <tr key={client.id}>
                <td>{client.name}</td>
                <td>{client.email}</td>
                <td>
                  <button onClick={() => handleEdit(client.id)}>Modifier</button>
                  <button onClick={() => handleDelete(client.id)}>Supprimer</button>
                  <button onClick={() => handleShow(client.id)}>Afficher</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </main>
    </>
  );
}

export default ClientTable;
