import { useLocation } from "react-router-dom";
import axios from "axios";

function Facturation(props) {
    const location = useLocation();
  const state = location.state || {};
  const { clientName, clientAddress } = state;


  const [articles, setArticles] = useState([]);
  const [selectedArticle, setSelectedArticle] = useState("");

  useEffect(() => {
    // Ici, vous pouvez appeler votre fonction pour récupérer les articles
    fetchArticles();
  }, []);
  return (
    <div>
      <h2>Facturation</h2>
      <label>Nom du client:</label>
      <input type="text" value={clientName} disabled />
      <label>Adresse du client:</label>
      <input type="text" value={clientAddress} disabled />
      <label>Choisir un article:</label>
      <select value={selectedArticle} onChange={(e) => setSelectedArticle(e.target.value)}>
        <option value="">Sélectionnez un article</option>
        {/* Ajoutez ici vos options d'article */}
      </select>
    </div>
  );
}

export default Facturation;
