import { useState } from "react";
import ClientDetails from "./components/ClientDetails";
import Dates from "./components/Dates";
import Footer from "./components/Footer";
import Heeader from "./components/Heeader";
import MainDetails from "./components/MainDetails";
import Notes from "./components/Notes";
import Table from "./components/Table";

function App() {
  const [showInvoice, setShowInvoice] = useState(false)
  const handlePrint = () => {
    window.print()
  }
  return (
    <>
      <main className="m-5 p-5 xl:max-w-4xl xl:mx-auto bg-white rounded shadow">
        {showInvoice ? <div>
          <Heeader handlePrint={handlePrint}/>
        <MainDetails/>
        <ClientDetails/>
        <Dates/>
        <Table/>
         <Notes/>
        <Footer/>
        <button>Changer Informations</button>
        </div>: (
          <>
          <div className="flex flex-col justify-center">
          <input type="text" name="text" id="text" placeholder="Your name" autoComplete="off" />
          <button onClick={()=> setShowInvoice(true)}
          className="bg-blue-500 text-white font-bold py-2 px-8 rounded shadow border-2 border-blue-500 
          hover:bg-transparent hover:text-bleu-500 
          transition-all duration-300">Aper√ßu de la facture</button>

          </div>
          </>
        )}
      </main>
    </>
  );
}

export default App;
