import React, { useState, useEffect } from "react";
import axios from "axios";
import EditClientForm from "./EditClientForm";
import { useNavigate, useParams } from "react-router-dom";

function EditClientPage() {
  const [editingClient, setEditingClient] = useState(null);
  const navigate = useNavigate();
  const { id } = useParams(); // Récupérer l'ID du client à partir des paramètres d'URL

  useEffect(() => {
    // Load client data to edit
    const fetchClientData = async () => {
      try {
        // Fetch client data from API
        const response = await axios.get(`http://localhost:3000/api/clients/${id}`);
        setEditingClient(response.data);
      } catch (error) {
        console.error("Error fetching client data:", error);
      }
    };

    // Call fetchClientData when component mounts
    fetchClientData();
  }, [id]); // Ajouter id comme dépendance pour recharger les données du client lorsque l'ID change

  const handleEditSubmit = async (formData) => {
    try {
      // Submit edited client data to API
      await axios.put(`http://localhost:3000/api/clients/${editingClient.id}`, formData);
      
      // Redirect to client list page after successful edit
      navigate("/client-list");
    } catch (error) {
      console.error("Error updating client:", error);
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h2 className="text-3xl font-semibold mb-4">Modifier Client</h2>
      {editingClient && <EditClientForm onSubmit={handleEditSubmit} clientData={editingClient} />}
    </div>
  );
}

export default EditClientPage;
