import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import ReactToPrint from "react-to-print";

function Facturation(props) {
   
    const location = useLocation();
  const state = location.state || {};
  const { clientName, clientAddress } = state;


  const [articles, setArticles] = useState([]);
  const [selectedArticle, setSelectedArticle] = useState("");
  const [selectedQuantity, setSelectedQuantity] = useState(1);
  useEffect(() => {
    // Ici, vous pouvez appeler votre fonction pour récupérer les articles
    fetchArticles();
  }, []);

  const fetchArticles = async () => {
    try {
      // Remplacez l'URL ci-dessous par l'URL de votre endpoint pour récupérer les articles depuis votre backend
      const response = await axios.get("http://localhost:3000/api/article");
      setArticles(response.data);
    } catch (error) {
      console.error("Error fetching articles:", error);
    }
  };
  const addArticle = () => {
    const selectedArticleObj = articles.find(article => article.id === selectedArticle);
    
    if (selectedArticleObj) {
        // Add the selected article to invoiceItems
        const newItem = {
            id: selectedArticleObj.id,
            description: selectedArticleObj.description,
            quantity: selectedQuantity,
            price: selectedArticleObj.prix,
            amount: selectedQuantity * selectedArticleObj.prix
        };
        setInvoiceItems([...invoiceItems, newItem]);
    }
}



  const handlePrint = () => {
    window.print();
  };
  
  return (
    <>
    <main className="m-5 p-5 md:max-w-xl md:mx-auto lg:max-w-2xl xl:max-w-4xl bg-white rounded shadow">
      <div className="invoice__preview bg-white p-5 rounded-2xl border-4 border-blue-200">
        <ReactToPrint
          trigger={() => (
            <button className="bg-blue-500 ml-5 text-white font-bold py-2 px-8 rounded hover:bg-blue-600 hover:text-white transition-all duration-150 hover:ring-4 hover:ring-blue-400">
              Print / Download
            </button>
          )}
          
        />
        <div  className="p-5">
          <header className="flex flex-col items-center justify-center mb-5 xl:flex-row xl:justify-between">
            <div>
              <h1 className="font-bold uppercase tracking-wide text-4xl mb-3">
                Facture
              </h1>
            </div>
          </header>

          <section className="flex flex-col items-end justify-end">
            <h2 className="font-bold text-3xl uppercase mb-1">LIMIMA</h2>
            <p>Msaken Sousse</p>
          </section>

          <section className="mt-10">
            <h2 className="text-2xl uppercase font-bold mb-1">{clientName}</h2>
            <p>{clientAddress}</p>
          </section>

          <article className="mt-10 mb-14 flex items-end justify-end">
            <ul>
              <li className="p-1">
                <span className="font-bold">Invoicer number:</span> 
              </li>
              <li className="p-1 bg-gray-100">
                <span className="font-bold">Invoice date:</span> 
              </li>
              
            </ul>
          </article>
          <div className="flex items-center mb-4">
        <label className="mr-4">Choisir un article:</label>
        <select
          value={selectedArticle}
          onChange={(e) => setSelectedArticle(e.target.value)}
          className="mr-4"
        >
          <option value="">Sélectionnez un article</option>
          {/* Mapping through articles to create options */}
        </select>
        <input
          type="number"
          value={selectedQuantity}
          onChange={(e) => setSelectedQuantity(e.target.value)}
          placeholder="Quantité"
        />
        <button onClick={addArticle} className="bg-blue-500 text-white font-bold py-2 px-4 rounded ml-4">
          Ajouter
        </button>
      </div>
      
      <table className="w-full mb-4">
        <thead>
          <tr className="bg-gray-100">
            <th className="px-4 py-2">Description</th>
            <th className="px-4 py-2">Quantité</th>
            <th className="px-4 py-2">Prix</th>
            <th className="px-4 py-2">Montant</th>
          </tr>
        </thead>
        <tbody>
          {invoiceItems.map((item) => (
            <tr key={item.id}>
              <td className="border px-4 py-2">{item.description}</td>
              <td className="border px-4 py-2">{item.quantity}</td>
              <td className="border px-4 py-2">{item.price}</td>
              <td className="border px-4 py-2">{item.amount}</td>
            </tr>
          ))}
        </tbody>
      </table>
      </div>
      </div>
      </main>
    </>
  );
}


export default Facturation;
