import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Navigate } from 'react-router-dom';

const FacturationForm = () => {
    const [facturations, setFacturations] = useState([]);
    const [deletedFacturationId, setDeletedFacturationId] = useState(null);
    const [deleteMsg, setDeleteMsg] = useState(false);

    useEffect(() => {
        fetchFacturations();
    }, []);

    const handleViewInvoice = async (idInvoice) => {
        try {
            Navigate(`/consulter/${idInvoice}`);
        } catch (error) {
            console.error("Error viewing invoice:", error);
        }
    };

    const fetchFacturations = async () => {
        try {
            const response = await axios.get("http://localhost:3000/api/fact");
            const facturationsWithArticleDetails = await Promise.all(response.data.map(async (facturation) => {
                const articles = await fetchArticleDetails(facturation.articles);
                const clientId = facturation.client;
                const clientResponse = await axios.get(`http://localhost:3000/api/client/${clientId}`);
                const clientName = clientResponse.data.name;
                return { ...facturation, client: clientName, articles };
            }));

            setFacturations(facturationsWithArticleDetails);
        } catch (error) {
            console.error("Error fetching facturations:", error);
        }
    };

    const handleDelete = async (facturationId) => {
        try {
            await axios.delete(`/fact/${facturationId}`);
            fetchFacturations();
            setDeletedFacturationId(facturationId);
            setDeleteMsg(true);
        } catch (error) {
            console.error("Error deleting facturation:", error);
        }
    };

    const fetchArticleDetails = async (articleIds) => {
        try {
            const articles = await Promise.all(articleIds.map(async (articleId) => {
                const response = await axios.get(`/article/${articleId}`);
                return response.data;
            }));
            return articles;
        } catch (error) {
            console.error("Error fetching article details:", error);
            return [];
        }
    };

    return (
        <div>
            <h2>List of Factures</h2>
            {deleteMsg && <p>Facturation with ID {deletedFacturationId} was successfully deleted.</p>}
            <table>
                <thead>
                    <tr>
                        <th>Facture Number</th>
                        <th>Facture Date</th>
                        <th>Client</th>
                        <th>Articles</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {facturations.map(facturation => (
                        <tr key={facturation.id}>
                            <td>{facturation.numeroFacture}</td>
                            <td>{facturation.dateFacture}</td>
                            <td>{facturation.client}</td>
                            <td>
                                <ul>
                                    {facturation.articles.map(article => (
                                        <li key={article.id}>
                                            {article.name} - {article.reference}
                                        </li>
                                    ))}
                                </ul>
                            </td>
                            <td>
                                <button onClick={() => handleDelete(facturation.id)}>Delete</button>
                                <button onClick={() => handleViewInvoice(facturation.id)}>View Invoice</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default FacturationForm;
